import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { DashboardProps, SensorData } from '../types';
import { FaWater, FaCloud, FaLightbulb, FaWalking, FaSmog } from 'react-icons/fa';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard: React.FC<DashboardProps> = ({ token, setToken, devices }) => {
  const [availableDevices, setAvailableDevices] = useState<string[]>([]);
  const [selectedDevice, setSelectedDevice] = useState<string>(devices[0] || '');
  const [data, setData] = useState<SensorData | null>(null);
  const [history, setHistory] = useState<SensorData[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [showHistory, setShowHistory] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAvailableDevices = async () => {
      try {
        setLoading(true);
        const response = await axios.get<string[]>('http://localhost:5000/api/data/devices', {
          headers: { 'x-auth-token': token },
        });
        setAvailableDevices(response.data);
        if (selectedDevice && !response.data.includes(selectedDevice)) {
          const deviceWithData = response.data.find(dev => dev === 'b8:27:eb:bf:9d:51');
          if (deviceWithData) await changeDevice(deviceWithData);
        }
      } catch (err) {
        console.error('Error al obtener dispositivos disponibles:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchAvailableDevices();
    const interval = setInterval(fetchAvailableDevices, 300000);
    return () => clearInterval(interval);
  }, [token, selectedDevice]);

  useEffect(() => {
    const fetchData = async () => {
      if (!selectedDevice) return;
      try {
        setLoading(true);
        const response = await axios.get<SensorData>(`http://localhost:5000/api/data/realtime/${selectedDevice}`, {
          headers: { 'x-auth-token': token },
        });
        setData(response.data);
      } catch (err) {
        console.error('Error al obtener datos en tiempo real:', err);
        setData(null);
      } finally {
        setLoading(false);
      }
    };

    const fetchHistory = async () => {
      if (!selectedDevice) return;
      try {
        setLoading(true);
        const response = await axios.get<SensorData[]>(`http://localhost:5000/api/data/history/${selectedDevice}`, {
          headers: { 'x-auth-token': token },
        });
        setHistory(response.data);
      } catch (err) {
        console.error('Error al obtener el historial:', err);
        setHistory([]);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    fetchHistory();
    const interval = setInterval(() => {
      fetchData();
      fetchHistory();
    }, 300000);
    return () => clearInterval(interval);
  }, [selectedDevice, token]);

  const updateHistoryChartData = (dataKey: string) => {
    if (history.length > 0) {
      const labels = history.map((_, index) => `Punto ${index + 1}`);
      return {
        labels,
        datasets: [
          {
            label: dataKey.charAt(0).toUpperCase() + dataKey.slice(1),
            data: history.map(item => item[dataKey as keyof SensorData]),
            borderColor: getColor(dataKey),
            backgroundColor: getColor(dataKey, 0.2),
            tension: 0.3,
            fill: false,
          },
        ],
      };
    }
    return { labels: [], datasets: [] };
  };

  const getColor = (dataKey: string, opacity: number = 1) => {
    const colors: { [key: string]: string } = {
      temp: 'rgb(75, 192, 192)',
      humidity: 'rgb(255, 99, 132)',
      co: 'rgb(54, 162, 235)',
      lpg: 'rgb(255, 206, 86)',
      light: 'rgb(153, 102, 255)',
      motion: 'rgb(255, 159, 64)',
      smoke: 'rgb(201, 203, 207)',
    };
    return colors[dataKey] ? colors[dataKey].replace('rgb', 'rgba').replace(')', `, ${opacity})`) : 'gray';
  };

  const handleLogout = () => {
    setToken(null);
    navigate('/login');
  };

  const changeDevice = async (newDevice: string) => {
    if (!newDevice || newDevice === selectedDevice) return;
    try {
      setLoading(true);
      if (selectedDevice) {
        await axios.post('http://localhost:5000/api/auth/devices/remove', { device: selectedDevice }, {
          headers: { 'x-auth-token': token },
        });
      }
      await axios.post('http://localhost:5000/api/auth/devices/add', { device: newDevice }, {
        headers: { 'x-auth-token': token },
      });
      setSelectedDevice(newDevice);
      setToken(token, [newDevice]);
    } catch (err) {
      console.error('Error al cambiar de dispositivo:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-lg font-semibold text-gray-600 animate-pulse">Cargando...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-gray-100 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-10">
          <h1 className="text-4xl font-extrabold text-gray-800">Tablero de Control</h1>
          <button
            onClick={handleLogout}
            className="bg-red-600 text-white px-5 py-2 rounded-lg shadow hover:bg-red-700 transition duration-200"
          >
            Cerrar Sesión
          </button>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Sección de Cambio de Dispositivo */}
          <div className="lg:col-span-3 bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-semibold text-gray-800 mb-4">Cambiar Dispositivo</h2>
            <p className="text-gray-600 mb-4">
              Dispositivo Actual: <span className="font-medium text-blue-600">{selectedDevice || 'Ninguno'}</span>
            </p>
            <div className="flex flex-wrap gap-3">
              {availableDevices
                .filter(dev => dev !== selectedDevice)
                .map(dev => (
                  <button
                    key={dev}
                    onClick={() => changeDevice(dev)}
                    className="bg-blue-500 text-white px-4 py-2 rounded-lg shadow hover:bg-blue-600 transition duration-200"
                  >
                    Cambiar a {dev}
                  </button>
                ))}
            </div>
          </div>

          {/* Datos en Tiempo Real */}
          <div className="bg-white p-6 rounded-xl shadow-lg lg:col-span-2">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6">Datos en Tiempo Real</h2>
            {data ? (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {/* Temperatura - Termómetro */}
                <div className="p-4 bg-blue-50 rounded-lg group">
                  <h3 className="text-lg font-medium text-blue-700 mb-2">Temperatura</h3>
                  <div className="relative w-20 h-64 mx-auto">
                    <div className="thermometer w-full h-full border-2 border-gray-300 rounded-t-full bg-gray-100 overflow-hidden">
                      <div
                        className="thermometer-fill bg-gradient-to-t from-red-600 to-red-400"
                        style={{ height: `${(data.temp / 50) * 100}%`, transition: 'height 0.5s ease' }}
                      />
                      <span className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-lg font-bold text-gray-800">
                        {data.temp}°C
                      </span>
                    </div>
                    <div className="thermometer-bulb absolute bottom-0 left-1/2 transform -translate-x-1/2 w-12 h-12 bg-red-600 rounded-full border-2 border-gray-300 transition-transform duration-200 group-hover:scale-110" />
                  </div>
                  <button
                    onClick={() => setShowHistory('temp')}
                    className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-200 w-full"
                  >
                    Ver Historial
                  </button>
                </div>

                {/* Humedad - Gota */}
                <div className="p-4 bg-blue-50 rounded-lg">
                  <h3 className="text-lg font-medium text-blue-700 mb-2">Humedad</h3>
                  <div className="relative w-28 h-28 mx-auto">
                    <FaWater className="w-full h-full text-blue-400 animate-pulse" />
                    <span className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-2xl font-bold text-blue-600">
                      {data.humidity}%
                    </span>
                  </div>
                  <button
                    onClick={() => setShowHistory('humidity')}
                    className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-200 w-full"
                  >
                    Ver Historial
                  </button>
                </div>

                {/* CO - Nube */}
                <div className="p-4 bg-blue-50 rounded-lg">
                  <h3 className="text-lg font-medium text-blue-700 mb-2">CO</h3>
                  <div className="relative w-28 h-28 mx-auto">
                    <FaCloud className="w-full h-full text-gray-400" style={{ opacity: Math.min(data.co / 100, 1) }} />
                    <span className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-2xl font-bold text-gray-700">
                      {data.co}
                    </span>
                  </div>
                  <button
                    onClick={() => setShowHistory('co')}
                    className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-200 w-full"
                  >
                    Ver Historial
                  </button>
                </div>

                {/* LPG - Medidor Circular */}
                <div className="p-4 bg-blue-50 rounded-lg">
                  <h3 className="text-lg font-medium text-blue-700 mb-2">LPG</h3>
                  <div className="relative w-28 h-28 mx-auto">
                    <svg className="w-full h-full" viewBox="0 0 36 36">
                      <path
                        d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                        fill="none"
                        stroke="#e5e7eb"
                        strokeWidth="2"
                      />
                      <path
                        strokeDasharray={`${(data.lpg / 100) * 100}, 100`}
                        d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                        fill="none"
                        stroke="#facc15"
                        strokeWidth="2"
                        strokeLinecap="round"
                        className="transition-all duration-500"
                      />
                      <text x="18" y="20.35" className="text-xl font-bold text-yellow-600" textAnchor="middle">
                        {data.lpg}
                      </text>
                    </svg>
                  </div>
                  <button
                    onClick={() => setShowHistory('lpg')}
                    className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-200 w-full"
                  >
                    Ver Historial
                  </button>
                </div>

                {/* Luz - Bombilla */}
                <div className="p-4 bg-blue-50 rounded-lg">
                  <h3 className="text-lg font-medium text-blue-700 mb-2">Luz</h3>
                  <div className="text-center">
                    <FaLightbulb
                      className={`text-6xl mx-auto ${data.light ? 'text-yellow-400 animate-glow' : 'text-gray-400'}`}
                    />
                    <span className="block mt-2 text-lg font-medium">{data.light ? 'Encendido' : 'Apagado'}</span>
                  </div>
                  <button
                    onClick={() => setShowHistory('light')}
                    className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-200 w-full"
                  >
                    Ver Historial
                  </button>
                </div>

                {/* Movimiento - Icono */}
                <div className="p-4 bg-blue-50 rounded-lg">
                  <h3 className="text-lg font-medium text-blue-700 mb-2">Movimiento</h3>
                  <div className="text-center">
                    <FaWalking
                      className={`text-6xl mx-auto ${data.motion ? 'text-orange-400 animate-bounce' : 'text-gray-400'}`}
                    />
                    <span className="block mt-2 text-lg font-medium">{data.motion ? 'Sí' : 'No'}</span>
                  </div>
                  <button
                    onClick={() => setShowHistory('motion')}
                    className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-200 w-full"
                  >
                    Ver Historial
                  </button>
                </div>

                {/* Humo - Nube */}
                <div className="p-4 bg-blue-50 rounded-lg">
                  <h3 className="text-lg font-medium text-blue-700 mb-2">Humo</h3>
                  <div className="relative w-28 h-28 mx-auto">
                    <FaSmog
                      className={`w-full h-full ${data.smoke ? 'text-gray-500 animate-pulse' : 'text-gray-300'}`}
                    />
                    <span className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-2xl font-bold text-gray-700">
                      {data.smoke ? 'Sí' : 'No'}
                    </span>
                  </div>
                  <button
                    onClick={() => setShowHistory('smoke')}
                    className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition duration-200 w-full"
                  >
                    Ver Historial
                  </button>
                </div>

                <div className="p-3 bg-blue-50 rounded-lg col-span-full">
                  <strong className="text-blue-700">Última actualización:</strong>{' '}
                  {new Date(data.ts).toLocaleString('es-ES')}
                </div>
              </div>
            ) : (
              <p className="text-gray-500 text-center">No hay datos disponibles.</p>
            )}
          </div>

          {/* Historial (Modal) */}
          {showHistory && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-4xl">
                <h2 className="text-2xl font-semibold text-gray-800 mb-4">
                  Historial de {showHistory.charAt(0).toUpperCase() + showHistory.slice(1)}
                </h2>
                <div className="h-96">
                  <Line
                    data={updateHistoryChartData(showHistory)}
                    options={{
                      responsive: true,
                      maintainAspectRatio: false,
                      plugins: {
                        legend: { position: 'top' },
                        title: { display: true, text: `Historial de ${showHistory}` },
                      },
                      scales: {
                        x: { title: { display: true, text: 'Punto' } },
                        y: { title: { display: true, text: 'Valor' } },
                      },
                    }}
                  />
                </div>
                <button
                  onClick={() => setShowHistory(null)}
                  className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition duration-200"
                >
                  Cerrar
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
