  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { DashboardProps, SensorData } from '../types';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard: React.FC<DashboardProps> = ({ token, setToken, devices }) => {
  const [availableDevices, setAvailableDevices] = useState<string[]>([]);
  const [selectedDevice, setSelectedDevice] = useState<string>(devices[0] || '');
  const [data, setData] = useState<SensorData | null>(null);
  const [history, setHistory] = useState<SensorData[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [showHistory, setShowHistory] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAvailableDevices = async () => {
      try {
        setLoading(true);
        const response = await axios.get<string[]>('http://localhost:5000/api/data/devices', {
          headers: { 'x-auth-token': token },
        });
        setAvailableDevices(response.data);
        if (selectedDevice && !response.data.includes(selectedDevice)) {
          const deviceWithData = response.data.find(dev => dev === 'b8:27:eb:bf:9d:51');
          if (deviceWithData) await changeDevice(deviceWithData);
        }
      } catch (err) {
        console.error('Error al obtener dispositivos disponibles:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchAvailableDevices();
    const interval = setInterval(fetchAvailableDevices, 300000);
    return () => clearInterval(interval);
  }, [token, selectedDevice]);

  useEffect(() => {
    const fetchData = async () => {
      if (!selectedDevice) return;
      try {
        setLoading(true);
        const response = await axios.get<SensorData>(`http://localhost:5000/api/data/realtime/${selectedDevice}`, {
          headers: { 'x-auth-token': token },
        });
        setData(response.data);
      } catch (err) {
        console.error('Error al obtener datos en tiempo real:', err);
        setData(null);
      } finally {
        setLoading(false);
      }
    };

    const fetchHistory = async () => {
      if (!selectedDevice) return;
      try {
        setLoading(true);
        const response = await axios.get<SensorData[]>(`http://localhost:5000/api/data/history/${selectedDevice}`, {
          headers: { 'x-auth-token': token },
        });
        setHistory(response.data);
      } catch (err) {
        console.error('Error al obtener el historial:', err);
        setHistory([]);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    fetchHistory();
    const interval = setInterval(() => {
      fetchData();
      fetchHistory();
    }, 300000);
    return () => clearInterval(interval);
  }, [selectedDevice, token]);

  const updateHistoryChartData = (dataKey: string) => {
    if (history.length > 0) {
      const labels = history.map((_, index) => `Punto ${index + 1}`);
      return {
        labels,
        datasets: [
          {
            label: dataKey.charAt(0).toUpperCase() + dataKey.slice(1),
            data: history.map(item => item[dataKey as keyof SensorData]),
            borderColor: getColor(dataKey),
            backgroundColor: getColor(dataKey, 0.2),
            tension: 0.3,
            fill: false,
          },
        ],
      };
    }
    return { labels: [], datasets: [] };
  };

  const getColor = (dataKey: string, opacity: number = 1) => {
    const colors: { [key: string]: string } = {
      temp: 'rgb(59, 130, 246)', // Azul profesional
      humidity: 'rgb(16, 185, 129)', // Verde esmeralda
      co: 'rgb(239, 68, 68)', // Rojo alerta
      lpg: 'rgb(245, 158, 11)', // Ámbar
      light: 'rgb(234, 179, 8)', // Amarillo suave
      motion: 'rgb(168, 85, 247)', // Púrpura
      smoke: 'rgb(107, 114, 128)', // Gris oscuro
    };
    return colors[dataKey] ? colors[dataKey].replace('rgb', 'rgba').replace(')', `, ${opacity})`) : 'gray';
  };

  const handleLogout = () => {
    setToken(null);
    navigate('/login');
  };

  const changeDevice = async (newDevice: string) => {
    if (!newDevice || newDevice === selectedDevice) return;
    try {
      setLoading(true);
      if (selectedDevice) {
        await axios.post('http://localhost:5000/api/auth/devices/remove', { device: selectedDevice }, {
          headers: { 'x-auth-token': token },
        });
      }
      await axios.post('http://localhost:5000/api/auth/devices/add', { device: newDevice }, {
        headers: { 'x-auth-token': token },
      });
      setSelectedDevice(newDevice);
      setToken(token, [newDevice]);
    } catch (err) {
      console.error('Error al cambiar de dispositivo:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="text-lg font-semibold text-gray-600 animate-pulse">Cargando...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition duration-200"
          >
            Cerrar Sesión
          </button>
        </div>

        {/* Sección de Dispositivos */}
        <div className="bg-white p-6 rounded-lg shadow-md mb-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Dispositivo Seleccionado</h2>
          <p className="text-gray-600 mb-4">
            Actual: <span className="font-medium text-blue-600">{selectedDevice || 'Ninguno'}</span>
          </p>
          <div className="flex flex-wrap gap-3">
            {availableDevices
              .filter(dev => dev !== selectedDevice)
              .map(dev => (
                <button
                  key={dev}
                  onClick={() => changeDevice(dev)}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition duration-200"
                >
                  {dev}
                </button>
              ))}
          </div>
        </div>

        {/* Datos en Tiempo Real */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {data ? (
            <>
              {/* Temperatura */}
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Temperatura</h3>
                <div className="flex items-center justify-between">
                  <div className="text-3xl font-bold text-blue-600">{data.temp}°C</div>
                  <div className="w-1/2 h-2 bg-gray-200 rounded-full overflow-hidden">
                    <div
                      className="h-full bg-blue-500"
                      style={{ width: `${(data.temp / 50) * 100}%`, transition: 'width 0.5s ease' }}
                    />
                  </div>
                </div>
                <button
                  onClick={() => setShowHistory('temp')}
                  className="mt-4 text-sm text-blue-500 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Humedad */}
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Humedad</h3>
                <div className="flex items-center justify-between">
                  <div className="text-3xl font-bold text-green-600">{data.humidity}%</div>
                  <div className="w-1/2 h-2 bg-gray-200 rounded-full overflow-hidden">
                    <div
                      className="h-full bg-green-500"
                      style={{ width: `${data.humidity}%`, transition: 'width 0.5s ease' }}
                    />
                  </div>
                </div>
                <button
                  onClick={() => setShowHistory('humidity')}
                  className="mt-4 text-sm text-blue-500 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* CO */}
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-800 mb-2">CO</h3>
                <div className="flex items-center justify-between">
                  <div className="text-3xl font-bold text-red-600">{data.co} ppm</div>
                  <div className="w-1/2 h-2 bg-gray-200 rounded-full overflow-hidden">
                    <div
                      className="h-full bg-red-500"
                      style={{ width: `${(data.co / 100) * 100}%`, transition: 'width 0.5s ease' }}
                    />
                  </div>
                </div>
                <button
                  onClick={() => setShowHistory('co')}
                  className="mt-4 text-sm text-blue-500 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* LPG */}
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-800 mb-2">LPG</h3>
                <div className="flex items-center justify-between">
                  <div className="text-3xl font-bold text-yellow-600">{data.lpg} ppm</div>
                  <div className="w-1/2 h-2 bg-gray-200 rounded-full overflow-hidden">
                    <div
                      className="h-full bg-yellow-500"
                      style={{ width: `${(data.lpg / 100) * 100}%`, transition: 'width 0.5s ease' }}
                    />
                  </div>
                </div>
                <button
                  onClick={() => setShowHistory('lpg')}
                  className="mt-4 text-sm text-blue-500 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Luz */}
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Luz</h3>
                <div className="text-3xl font-bold" style={{ color: data.light ? '#eab308' : '#6b7280' }}>
                  {data.light ? 'Encendido' : 'Apagado'}
                </div>
                <button
                  onClick={() => setShowHistory('light')}
                  className="mt-4 text-sm text-blue-500 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Movimiento */}
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Movimiento</h3>
                <div className="text-3xl font-bold" style={{ color: data.motion ? '#a855f7' : '#6b7280' }}>
                  {data.motion ? 'Detectado' : 'No Detectado'}
                </div>
                <button
                  onClick={() => setShowHistory('motion')}
                  className="mt-4 text-sm text-blue-500 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Humo */}
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-800 mb-2">Humo</h3>
                <div className="text-3xl font-bold" style={{ color: data.smoke ? '#6b7280' : '#9ca3af' }}>
                  {data.smoke ? 'Detectado' : 'No Detectado'}
                </div>
                <button
                  onClick={() => setShowHistory('smoke')}
                  className="mt-4 text-sm text-blue-500 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Última Actualización */}
              <div className="bg-white p-4 rounded-lg shadow-md col-span-full">
                <p className="text-gray-600">
                  <strong className="text-gray-800">Última actualización:</strong>{' '}
                  {new Date(data.ts).toLocaleString('es-ES')}
                </p>
              </div>
            </>
          ) : (
            <div className="col-span-full bg-white p-6 rounded-lg shadow-md text-center text-gray-500">
              No hay datos disponibles
            </div>
          )}
        </div>

        {/* Modal de Historial */}
        {showHistory && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-4xl">
              <h2 className="text-2xl font-semibold text-gray-800 mb-4">
                Historial de {showHistory.charAt(0).toUpperCase() + showHistory.slice(1)}
              </h2>
              <div className="h-96">
                <Line
                  data={updateHistoryChartData(showHistory)}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: { position: 'top' },
                      title: { display: true, text: `Historial de ${showHistory}` },
                    },
                    scales: {
                      x: { title: { display: true, text: 'Punto' } },
                      y: { title: { display: true, text: 'Valor' } },
                    },
                  }}
                />
              </div>
              <button
                onClick={() => setShowHistory(null)}
                className="mt-4 bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600 transition duration-200"
              >
                Cerrar
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
