import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { DashboardProps, SensorData } from '../types';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Dashboard: React.FC<DashboardProps> = ({ token, setToken, devices }) => {
  const [availableDevices, setAvailableDevices] = useState<string[]>([]);
  const [selectedDevice, setSelectedDevice] = useState<string>(devices[0] || '');
  const [data, setData] = useState<SensorData | null>(null);
  const [history, setHistory] = useState<SensorData[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [showHistory, setShowHistory] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAvailableDevices = async () => {
      try {
        setLoading(true);
        const response = await axios.get<string[]>('http://localhost:5000/api/data/devices', {
          headers: { 'x-auth-token': token },
        });
        setAvailableDevices(response.data);
        if (selectedDevice && !response.data.includes(selectedDevice)) {
          const deviceWithData = response.data.find(dev => dev === 'b8:27:eb:bf:9d:51');
          if (deviceWithData) await changeDevice(deviceWithData);
        }
      } catch (err) {
        console.error('Error al obtener dispositivos disponibles:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchAvailableDevices();
    const interval = setInterval(fetchAvailableDevices, 300000);
    return () => clearInterval(interval);
  }, [token, selectedDevice]);

  useEffect(() => {
    const fetchData = async () => {
      if (!selectedDevice) return;
      try {
        setLoading(true);
        const response = await axios.get<SensorData>(`http://localhost:5000/api/data/realtime/${selectedDevice}`, {
          headers: { 'x-auth-token': token },
        });
        setData(response.data);
      } catch (err) {
        console.error('Error al obtener datos en tiempo real:', err);
        setData(null);
      } finally {
        setLoading(false);
      }
    };

    const fetchHistory = async () => {
      if (!selectedDevice) return;
      try {
        setLoading(true);
        const response = await axios.get<SensorData[]>(`http://localhost:5000/api/data/history/${selectedDevice}`, {
          headers: { 'x-auth-token': token },
        });
        setHistory(response.data);
      } catch (err) {
        console.error('Error al obtener el historial:', err);
        setHistory([]);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    fetchHistory();
    const interval = setInterval(() => {
      fetchData();
      fetchHistory();
    }, 300000);
    return () => clearInterval(interval);
  }, [selectedDevice, token]);

  const updateHistoryChartData = (dataKey: string) => {
    if (history.length > 0) {
      const labels = history.map((_, index) => `Punto ${index + 1}`);
      return {
        labels,
        datasets: [
          {
            label: dataKey.charAt(0).toUpperCase() + dataKey.slice(1),
            data: history.map(item => item[dataKey as keyof SensorData]),
            borderColor: getColor(dataKey),
            backgroundColor: getColor(dataKey, 0.2),
            tension: 0.3,
            fill: false,
          },
        ],
      };
    }
    return { labels: [], datasets: [] };
  };

  const getColor = (dataKey: string, opacity: number = 1) => {
    const colors: { [key: string]: string } = {
      temp: 'rgb(59, 130, 246)', // Azul
      humidity: 'rgb(16, 185, 129)', // Verde
      co: 'rgb(239, 68, 68)', // Rojo
      lpg: 'rgb(245, 158, 11)', // Ámbar
      light: 'rgb(234, 179, 8)', // Amarillo
      motion: 'rgb(168, 85, 247)', // Púrpura
      smoke: 'rgb(107, 114, 128)', // Gris
    };
    return colors[dataKey] ? colors[dataKey].replace('rgb', 'rgba').replace(')', `, ${opacity})`) : 'gray';
  };

  const handleLogout = () => {
    setToken(null);
    navigate('/login');
  };

  const changeDevice = async (newDevice: string) => {
    if (!newDevice || newDevice === selectedDevice) return;
    try {
      setLoading(true);
      if (selectedDevice) {
        await axios.post('http://localhost:5000/api/auth/devices/remove', { device: selectedDevice }, {
          headers: { 'x-auth-token': token },
        });
      }
      await axios.post('http://localhost:5000/api/auth/devices/add', { device: newDevice }, {
        headers: { 'x-auth-token': token },
      });
      setSelectedDevice(newDevice);
      setToken(token, [newDevice]);
    } catch (err) {
      console.error('Error al cambiar de dispositivo:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900">
        <div className="text-lg font-semibold text-gray-400 animate-pulse">Cargando...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-gray-200 p-6">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">Sensor Dashboard</h1>
          <button
            onClick={handleLogout}
            className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition duration-200"
          >
            Cerrar Sesión
          </button>
        </div>

        {/* Sección de Dispositivos */}
        <div className="bg-gray-800 p-6 rounded-lg shadow-lg mb-6">
          <h2 className="text-xl font-semibold text-gray-200 mb-4">Dispositivo Seleccionado</h2>
          <p className="text-gray-400 mb-4">
            Actual: <span className="font-medium text-green-400">{selectedDevice || 'Ninguno'}</span>
          </p>
          <div className="flex flex-wrap gap-3">
            {availableDevices
              .filter(dev => dev !== selectedDevice)
              .map(dev => (
                <button
                  key={dev}
                  onClick={() => changeDevice(dev)}
                  className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition duration-200"
                >
                  {dev}
                </button>
              ))}
          </div>
        </div>

        {/* Datos en Tiempo Real */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {data ? (
            <>
              {/* Temperatura - Termómetro */}
              <div className="bg-gray-800 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-200 mb-2">Temperatura</h3>
                <div className="flex justify-center mb-4">
                  <svg width="80" height="200" viewBox="0 0 80 200" className="thermometer">
                    <rect x="30" y="20" width="20" height="150" fill="#e5e7eb" rx="10" />
                    <rect
                      x="30"
                      y={170 - (data.temp / 50) * 150}
                      width="20"
                      height={(data.temp / 50) * 150}
                      fill="#3b82f6"
                      rx="10"
                      className="transition-all duration-500"
                    />
                    <circle cx="40" cy="170" r="15" fill="#3b82f6" />
                    <text x="40" y="100" fill="#ffffff" fontSize="14" textAnchor="middle">
                      {data.temp}°C
                    </text>
                  </svg>
                </div>
                <button
                  onClick={() => setShowHistory('temp')}
                  className="text-sm text-green-400 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Humedad - Gauge Semicircular */}
              <div className="bg-gray-800 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-200 mb-2">Humedad</h3>
                <div className="flex justify-center mb-4">
                  <svg width="120" height="80" viewBox="0 0 120 80">
                    <path
                      d="M20 60 A40 40 0 0 1 100 60"
                      fill="none"
                      stroke="#e5e7eb"
                      strokeWidth="10"
                    />
                    <path
                      d="M20 60 A40 40 0 0 1 100 60"
                      fill="none"
                      stroke="#10b981"
                      strokeWidth="10"
                      strokeDasharray={`${(data.humidity / 100) * 125.6}, 125.6`}
                      className="transition-all duration-500"
                    />
                    <text x="60" y="50" fill="#ffffff" fontSize="16" textAnchor="middle">
                      {data.humidity}%
                    </text>
                  </svg>
                </div>
                <button
                  onClick={() => setShowHistory('humidity')}
                  className="text-sm text-green-400 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* CO - Gauge Circular */}
              <div className="bg-gray-800 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-200 mb-2">CO</h3>
                <div className="flex justify-center mb-4">
                  <svg width="120" height="120" viewBox="0 0 120 120">
                    <circle cx="60" cy="60" r="50" fill="none" stroke="#e5e7eb" strokeWidth="10" />
                    <circle
                      cx="60"
                      cy="60"
                      r="50"
                      fill="none"
                      stroke={data.co > 50 ? '#ef4444' : data.co > 20 ? '#f59e0b' : '#10b981'}
                      strokeWidth="10"
                      strokeDasharray={`${(data.co / 100) * 314}, 314`}
                      className="transition-all duration-500"
                    />
                    <text x="60" y="65" fill="#ffffff" fontSize="16" textAnchor="middle">
                      {data.co} ppm
                    </text>
                  </svg>
                </div>
                <button
                  onClick={() => setShowHistory('co')}
                  className="text-sm text-green-400 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* LPG - Gauge Circular */}
              <div className="bg-gray-800 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-200 mb-2">LPG</h3>
                <div className="flex justify-center mb-4">
                  <svg width="120" height="120" viewBox="0 0 120 120">
                    <circle cx="60" cy="60" r="50" fill="none" stroke="#e5e7eb" strokeWidth="10" />
                    <circle
                      cx="60"
                      cy="60"
                      r="50"
                      fill="none"
                      stroke={data.lpg > 50 ? '#ef4444' : data.lpg > 20 ? '#f59e0b' : '#10b981'}
                      strokeWidth="10"
                      strokeDasharray={`${(data.lpg / 100) * 314}, 314`}
                      className="transition-all duration-500"
                    />
                    <text x="60" y="65" fill="#ffffff" fontSize="16" textAnchor="middle">
                      {data.lpg} ppm
                    </text>
                  </svg>
                </div>
                <button
                  onClick={() => setShowHistory('lpg')}
                  className="text-sm text-green-400 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Luz */}
              <div className="bg-gray-800 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-200 mb-2">Luz</h3>
                <div className="text-center mb-4">
                  <div
                    className={`w-16 h-16 mx-auto rounded-full flex items-center justify-center ${
                      data.light ? 'bg-yellow-500' : 'bg-gray-600'
                    }`}
                  >
                    <span className="text-white font-semibold">{data.light ? 'ON' : 'OFF'}</span>
                  </div>
                </div>
                <button
                  onClick={() => setShowHistory('light')}
                  className="text-sm text-green-400 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Movimiento */}
              <div className="bg-gray-800 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-200 mb-2">Movimiento</h3>
                <div className="text-center mb-4">
                  <div
                    className={`w-16 h-16 mx-auto rounded-full flex items-center justify-center ${
                      data.motion ? 'bg-purple-500' : 'bg-gray-600'
                    }`}
                  >
                    <span className="text-white font-semibold">{data.motion ? 'Sí' : 'No'}</span>
                  </div>
                </div>
                <button
                  onClick={() => setShowHistory('motion')}
                  className="text-sm text-green-400 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Humo */}
              <div className="bg-gray-800 p-4 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-200 mb-2">Humo</h3>
                <div className="text-center mb-4">
                  <div
                    className={`w-16 h-16 mx-auto rounded-full flex items-center justify-center ${
                      data.smoke ? 'bg-gray-500' : 'bg-gray-600'
                    }`}
                  >
                    <span className="text-white font-semibold">{data.smoke ? 'Sí' : 'No'}</span>
                  </div>
                </div>
                <button
                  onClick={() => setShowHistory('smoke')}
                  className="text-sm text-green-400 hover:underline"
                >
                  Ver Historial
                </button>
              </div>

              {/* Última Actualización */}
              <div className="bg-gray-800 p-4 rounded-lg shadow-lg col-span-full">
                <p className="text-gray-400">
                  <strong className="text-gray-200">Última actualización:</strong>{' '}
                  {new Date(data.ts).toLocaleString('es-ES')}
                </p>
              </div>
            </>
          ) : (
            <div className="col-span-full bg-gray-800 p-6 rounded-lg shadow-lg text-center text-gray-400">
              No hay datos disponibles
            </div>
          )}
        </div>

        {/* Modal de Historial */}
        {showHistory && (
          <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
            <div className="bg-gray-800 p-6 rounded-lg shadow-lg w-full max-w-4xl">
              <h2 className="text-2xl font-semibold text-gray-200 mb-4">
                Historial de {showHistory.charAt(0).toUpperCase() + showHistory.slice(1)}
              </h2>
              <div className="h-96">
                <Line
                  data={updateHistoryChartData(showHistory)}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: { position: 'top', labels: { color: '#ffffff' } },
                      title: {
                        display: true,
                        text: `Historial de ${showHistory}`,
                        color: '#ffffff',
                      },
                    },
                    scales: {
                      x: { title: { display: true, text: 'Punto', color: '#ffffff' }, ticks: { color: '#ffffff' } },
                      y: { title: { display: true, text: 'Valor', color: '#ffffff' }, ticks: { color: '#ffffff' } },
                    },
                  }}
                />
              </div>
              <button
                onClick={() => setShowHistory(null)}
                className="mt-4 bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700 transition duration-200"
              >
                Cerrar
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
