import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { DashboardProps, SensorData } from '../types';

const Dashboard: React.FC<DashboardProps> = ({ token, setToken, devices }) => {
  const [availableDevices, setAvailableDevices] = useState<string[]>([]);
  const [selectedDevice, setSelectedDevice] = useState<string>(devices[0] || '');
  const [data, setData] = useState<SensorData | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAvailableDevices = async () => {
      try {
        setLoading(true);
        const response = await axios.get<string[]>('http://localhost:5000/api/data/devices', {
          headers: { 'x-auth-token': token },
        });
        setAvailableDevices(response.data);
        if (selectedDevice && !response.data.includes(selectedDevice)) {
          const deviceWithData = response.data.find(dev => dev === 'b8:27:eb:bf:9d:51');
          if (deviceWithData) await changeDevice(deviceWithData);
        }
      } catch (err) {
        console.error('Error al obtener dispositivos disponibles:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchAvailableDevices();
    const interval = setInterval(fetchAvailableDevices, 300000);
    return () => clearInterval(interval);
  }, [token, selectedDevice]);

  useEffect(() => {
    const fetchData = async () => {
      if (!selectedDevice) return;
      try {
        setLoading(true);
        const response = await axios.get<SensorData>(`http://localhost:5000/api/data/realtime/${selectedDevice}`, {
          headers: { 'x-auth-token': token },
        });
        setData(response.data);
      } catch (err) {
        console.error('Error al obtener datos en tiempo real:', err);
        setData(null);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 300000);
    return () => clearInterval(interval);
  }, [selectedDevice, token]);

  const handleLogout = () => {
    setToken(null);
    navigate('/login');
  };

  const changeDevice = async (newDevice: string) => {
    if (!newDevice || newDevice === selectedDevice) return;
    try {
      setLoading(true);
      if (selectedDevice) {
        await axios.post('http://localhost:5000/api/auth/devices/remove', { device: selectedDevice }, {
          headers: { 'x-auth-token': token },
        });
      }
      await axios.post('http://localhost:5000/api/auth/devices/add', { device: newDevice }, {
        headers: { 'x-auth-token': token },
      });
      setSelectedDevice(newDevice);
      setToken(token, [newDevice]);
    } catch (err) {
      console.error('Error al cambiar de dispositivo:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-white">
        <div className="text-lg font-semibold text-gray-600 animate-pulse">Cargando...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white p-6">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800">Dashboard</h1>
          <button
            onClick={handleLogout}
            className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition duration-200"
          >
            Cerrar Sesión
          </button>
        </div>

        {/* Sección de Dispositivos */}
        <div className="bg-gray-50 p-6 rounded-lg shadow-md mb-6">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Dispositivo Seleccionado</h2>
          <p className="text-gray-600 mb-4">
            Actual: <span className="font-medium text-blue-600">{selectedDevice || 'Ninguno'}</span>
          </p>
          <div className="flex flex-wrap gap-3">
            {availableDevices
              .filter(dev => dev !== selectedDevice)
              .map(dev => (
                <button
                  key={dev}
                  onClick={() => changeDevice(dev)}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition duration-200"
                >
                  {dev}
                </button>
              ))}
          </div>
        </div>

        {/* Datos en Tiempo Real */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {data ? (
            <>
              {/* Temperatura - Termómetro */}
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Temperatura</h3>
                <div className="flex justify-center mb-4">
                  <svg width="120" height="300" viewBox="0 0 120 300" className="thermometer">
                    <rect x="45" y="30" width="30" height="225" fill="#e5e7eb" rx="15" />
                    <rect
                      x="45"
                      y={255 - (data.temp / 50) * 225}
                      width="30"
                      height={(data.temp / 50) * 225}
                      fill="#3b82f6"
                      rx="15"
                      className="transition-all duration-500"
                    />
                    <circle cx="60" cy="255" r="20" fill="#3b82f6" />
                    <text x="60" y="280" fill="#1e293b" fontSize="20" textAnchor="middle">
                      {data.temp}°C
                    </text>
                  </svg>
                </div>
              </div>

              {/* Humedad - Barra de Progreso */}
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Humedad</h3>
                <div className="text-3xl font-bold text-green-600 mb-2">{data.humidity}%</div>
                <div className="w-full bg-gray-200 rounded-full h-6 overflow-hidden">
                  <div
                    className="bg-green-500 h-full rounded-full"
                    style={{ width: `${data.humidity}%`, transition: 'width 0.5s ease' }}
                  />
                </div>
              </div>

              {/* CO - Barra de Progreso */}
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">CO</h3>
                <div className="text-3xl font-bold text-red-600 mb-2">{data.co} ppm</div>
                <div className="w-full bg-gray-200 rounded-full h-6 overflow-hidden">
                  <div
                    className="bg-red-500 h-full rounded-full"
                    style={{ width: `${(data.co / 100) * 100}%`, transition: 'width 0.5s ease' }}
                  />
                </div>
              </div>

              {/* LPG - Barra de Progreso */}
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">LPG</h3>
                <div className="text-3xl font-bold text-yellow-600 mb-2">{data.lpg} ppm</div>
                <div className="w-full bg-gray-200 rounded-full h-6 overflow-hidden">
                  <div
                    className="bg-yellow-500 h-full rounded-full"
                    style={{ width: `${(data.lpg / 100) * 100}%`, transition: 'width 0.5s ease' }}
                  />
                </div>
              </div>

              {/* Luz */}
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Luz</h3>
                <div className="text-3xl font-bold" style={{ color: data.light ? '#eab308' : '#6b7280' }}>
                  {data.light ? 'Encendido' : 'Apagado'}
                </div>
              </div>

              {/* Movimiento */}
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Movimiento</h3>
                <div className="text-3xl font-bold" style={{ color: data.motion ? '#a855f7' : '#6b7280' }}>
                  {data.motion ? 'Detectado' : 'No Detectado'}
                </div>
              </div>

              {/* Humo */}
              <div className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Humo</h3>
                <div className="text-3xl font-bold" style={{ color: data.smoke ? '#6b7280' : '#9ca3af' }}>
                  {data.smoke ? 'Detectado' : 'No Detectado'}
                </div>
              </div>

              {/* Última Actualización */}
              <div className="bg-white p-6 rounded-lg shadow-md col-span-full">
                <p className="text-gray-600">
                  <strong className="text-gray-700">Última actualización:</strong>{' '}
                  {new Date(data.ts).toLocaleString('es-ES')}
                </p>
              </div>
            </>
          ) : (
            <div className="col-span-full bg-white p-6 rounded-lg shadow-md text-center text-gray-500">
              No hay datos disponibles
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
